const contributorSummaryStyle = {"resourceType":"CodeSystem","id":"contributor-summary-style","meta":{"lastUpdated":"2022-05-28T13:47:40.239+11:00","profile":["http://hl7.org/fhir/StructureDefinition/shareablecodesystem"]},"text":{"status":"generated","div":"<div xmlns=\"http://www.w3.org/1999/xhtml\">\n      \n      <p>This code system http://terminology.hl7.org/CodeSystem/contributor-summary-style defines the following codes:</p>\n      \n      <table class=\"codes\">\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">\n            \n            <b>Code</b>\n          \n          </td>\n          \n          <td>\n            \n            <b>Display</b>\n          \n          </td>\n          \n          <td>\n            \n            <b>Definition</b>\n          \n          </td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">a1full\n            \n            <a name=\"contributor-summary-style-a1full\"> </a>\n          \n          </td>\n          \n          <td>First author (full name) et al</td>\n          \n          <td>Example: Jennifer Laskowski et al.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">a1init\n            \n            <a name=\"contributor-summary-style-a1init\"> </a>\n          \n          </td>\n          \n          <td>First author (last name first initials) et al</td>\n          \n          <td>Example: Laskowski J et al.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">a3full\n            \n            <a name=\"contributor-summary-style-a3full\"> </a>\n          \n          </td>\n          \n          <td>First 3 authors (full name) et al</td>\n          \n          <td>Example: Jennifer Laskowski, Brandon Renner, Matthew C. Pickering, et al.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">a3init\n            \n            <a name=\"contributor-summary-style-a3init\"> </a>\n          \n          </td>\n          \n          <td>First 3 authors (last name first initials) et al</td>\n          \n          <td>Example:  Laskowski J, Renner B, Pickering MC, et al.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">a6full\n            \n            <a name=\"contributor-summary-style-a6full\"> </a>\n          \n          </td>\n          \n          <td>First 6 authors (full name) et al</td>\n          \n          <td>Example: Jennifer Laskowski, Brandon Renner, Matthew C. Pickering, Natalie J. Serkova, Peter M. Smith-Jones, Eric T. Clambey, et al.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">a6init\n            \n            <a name=\"contributor-summary-style-a6init\"> </a>\n          \n          </td>\n          \n          <td>First 6 authors (last name first initials) et al</td>\n          \n          <td>Example: Laskowski J, Renner B, Pickering MC, Serkova NJ, Smith-Jones PM, Clambey ET, et al.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">aallfull\n            \n            <a name=\"contributor-summary-style-aallfull\"> </a>\n          \n          </td>\n          \n          <td>All authors (full name)</td>\n          \n          <td>Example: Jennifer Laskowski, Brandon Renner, Matthew C. Pickering, Natalie J. Serkova, Peter M. Smith-Jones, Eric T. Clambey, Raphael A. Nemenoff, Joshua M. Thurman.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">aallfullwithand\n            \n            <a name=\"contributor-summary-style-aallfullwithand\"> </a>\n          \n          </td>\n          \n          <td>All authors (full name) with and before last author</td>\n          \n          <td>Example: Jennifer Laskowski, Brandon Renner, Matthew C. Pickering, Natalie J. Serkova, Peter M. Smith-Jones, Eric T. Clambey, Raphael A. Nemenoff, and Joshua M. Thurman.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">aallfullwithampersand\n            \n            <a name=\"contributor-summary-style-aallfullwithampersand\"> </a>\n          \n          </td>\n          \n          <td>All authors (full name) with an ampersand before last author</td>\n          \n          <td>Example: Jennifer Laskowski, Brandon Renner, Matthew C. Pickering, Natalie J. Serkova, Peter M. Smith-Jones, Eric T. Clambey, Raphael A. Nemenoff, &amp; Joshua M. Thurman.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">aallinit\n            \n            <a name=\"contributor-summary-style-aallinit\"> </a>\n          \n          </td>\n          \n          <td>All authors (last name first initials)</td>\n          \n          <td>Example: Laskowski J, Renner B, Pickering MC, Serkova NJ, Smith-Jones PM, Clambey ET, Nemenoff RA, Thurman JM.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">aallinitwithand\n            \n            <a name=\"contributor-summary-style-aallinitwithand\"> </a>\n          \n          </td>\n          \n          <td>All authors (last name first initials) with and before last author</td>\n          \n          <td>Example: Laskowski J, Renner B, Pickering MC, Serkova NJ, Smith-Jones PM, Clambey ET, Nemenoff RA, and Thurman JM.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">aallinitwithampersand\n            \n            <a name=\"contributor-summary-style-aallinitwithampersand\"> </a>\n          \n          </td>\n          \n          <td>All authors (last name first initials) with an ampersand before last author</td>\n          \n          <td>Example: Laskowski J, Renner B, Pickering MC, Serkova NJ, Smith-Jones PM, Clambey ET, Nemenoff RA, &amp; Thurman JM.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">contr-full-by-person\n            \n            <a name=\"contributor-summary-style-contr-full-by-person\"> </a>\n          \n          </td>\n          \n          <td>Contributorship statement listed by person with full names</td>\n          \n          <td>Jennnifer Laskowski designed project; developed and performed experiments; collected and analyzed data; wrote and revised manuscript. Brandon Renner performed critical review of manuscript. Matthew C. Pickering provided technical advice and contributed to study design. Natalie J. Serkova and Peter M. Smith-Jones performed PET imaging and associated analyses. Eric T. Clambey and Raphael A. Nemenoff provided conceptual and technical guidance and critical review of manuscript. Joshua M. Thurman contributed to experimental design and wrote manuscript.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">contr-init-by-person\n            \n            <a name=\"contributor-summary-style-contr-init-by-person\"> </a>\n          \n          </td>\n          \n          <td>Contributorship statement listed by person with initials</td>\n          \n          <td>J.L. designed project; developed and performed experiments; collected and analyzed data; wrote and revised manuscript. B.R. performed critical review of manuscript. M.C.P. provided technical advice and contributed to study design. N.J.S and P.M.S.-J. performed PET imaging and associated analyses. E.T.C. and R.A.N provided conceptual and technical guidance and critical review of manuscript. J.M.T contributed to experimental design and wrote manuscript.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">contr-full-by-contr\n            \n            <a name=\"contributor-summary-style-contr-full-by-contr\"> </a>\n          \n          </td>\n          \n          <td>Contributorship statement listed by contribution with full names</td>\n          \n          <td>Project design by Jennnifer Laskowski (JL), Experiment design by JL and Joshua M. Thurman (JMT), technical advice and study design contribution by Matthew C. Pickering, experiments performed by JL, data collection and analysis by JL, PET imaging and associated analyses by Natalie J. Serkova and Peter M. Smith-Jones, conceptual and technical guidance by Eric T. Clambey (ETC) and Raphael A. Nemenoff (RAN), manuscript writing by JL and JMT, manuscript revised critically by JL, Brandon Renner, ETC, and RAN.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">contr-init-by-contr\n            \n            <a name=\"contributor-summary-style-contr-init-by-contr\"> </a>\n          \n          </td>\n          \n          <td>Contributorship statement listed by contribution with initials</td>\n          \n          <td>Project design by JL, Experiment design by JL and JMT, technical advice and study design contribution by MCP, experiments performed by JL, data collection and analysis by JL, PET imaging and associated analyses by NJS and PMS-J, conceptual and technical guidance by ETC and RAN, manuscript writing by JL and JMT, manuscript revised critically by JL, BR, ETC, and RAN.</td>\n        \n        </tr>\n      \n      </table>\n    \n    </div>"},"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/structuredefinition-wg","valueCode":"cds"},{"url":"http://hl7.org/fhir/StructureDefinition/structuredefinition-standards-status","valueCode":"trial-use"},{"url":"http://hl7.org/fhir/StructureDefinition/structuredefinition-fmm","valueInteger":0}],"url":"http://terminology.hl7.org/CodeSystem/contributor-summary-style","identifier":[{"system":"u