const planDefinitionType = {"resourceType":"CodeSystem","id":"plan-definition-type","meta":{"lastUpdated":"2022-05-28T13:47:40.239+11:00","profile":["http://hl7.org/fhir/StructureDefinition/shareablecodesystem"]},"text":{"status":"generated","div":"<div xmlns=\"http://www.w3.org/1999/xhtml\">\n      \n      <p>This code system http://terminology.hl7.org/CodeSystem/plan-definition-type defines the following codes:</p>\n      \n      <table class=\"codes\">\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">\n            \n            <b>Code</b>\n          \n          </td>\n          \n          <td>\n            \n            <b>Display</b>\n          \n          </td>\n          \n          <td>\n            \n            <b>Definition</b>\n          \n          </td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">order-set\n            \n            <a name=\"plan-definition-type-order-set\"> </a>\n          \n          </td>\n          \n          <td>Order Set</td>\n          \n          <td>A pre-defined and approved group of orders related to a particular clinical condition (e.g. hypertension treatment and monitoring) or stage of care (e.g. hospital admission to Coronary Care Unit). An order set is used as a checklist for the clinician when managing a patient with a specific condition. It is a structured collection of orders relevant to that condition and presented to the clinician in a computerized provider order entry (CPOE) system.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">clinical-protocol\n            \n            <a name=\"plan-definition-type-clinical-protocol\"> </a>\n          \n          </td>\n          \n          <td>Clinical Protocol</td>\n          \n          <td>Defines a desired/typical sequence of clinical activities including preconditions, triggers and temporal relationships.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">eca-rule\n            \n            <a name=\"plan-definition-type-eca-rule\"> </a>\n          \n          </td>\n          \n          <td>ECA Rule</td>\n          \n          <td>A decision support rule of the form [on Event] if Condition then Action. It is intended to be a shareable, computable definition of actions that should be taken whenever some condition is met in response to a particular event or events.</td>\n        \n        </tr>\n        \n        <tr>\n          \n          <td style=\"white-space:nowrap\">workflow-definition\n            \n            <a name=\"plan-definition-type-workflow-definition\"> </a>\n          \n          </td>\n          \n          <td>Workflow Definition</td>\n          \n          <td>Defines the steps for a group of one or more systems in an event flow process along with the step constraints, sequence, pre-conditions and decision points to complete a particular objective.</td>\n        \n        </tr>\n      \n      </table>\n    \n    </div>"},"extension":[{"url":"http://hl7.org/fhir/StructureDefinition/structuredefinition-wg","valueCode":"cds"},{"url":"http://hl7.org/fhir/StructureDefinition/structuredefinition-standards-status","valueCode":"trial-use"},{"url":"http://hl7.org/fhir/StructureDefinition/structuredefinition-fmm","valueInteger":3}],"url":"http://terminology.hl7.org/CodeSystem/plan-definition-type","identifier":[{"system":"urn:ietf:rfc:3986","value":"urn:oid:2.16.840.1.113883.4.642.1.1245"}],"version":"4.3.0","name":"PlanDefinitionType","title":"PlanDefinitionType","status":"draft","experimental":false,"date":"2020-12-28T16:55:11+11:00","publisher":"HL7 (FHIR Project)","contact":[{"telecom":[{"system":"url","value":"http://hl7.org/fhir"},{"system":"email","value":"fhir@lists.hl7.org"}]}],"description":"The type of PlanDefinition.","caseSensitive":true,"valueSet":"http://hl7.org/fhir/ValueSet/plan-definitio